    public void assertMatrixEquals(File expectedCsv, DoubleMatrix2D actual) throws IOException {
        CSVFile expected = CSVFile.readFileNumeric(expectedCsv, true, 0);
        for (int r = 0; r < expected.getNumRows(); r++) {
            for (int c = 0; c < expected.getNumCols(); c++) {
                assertEquals("matrix[" + r + ", " + c + "] ", expected.getDouble(r, c), actual.get(r,c), 0.00000001);
            }
        }
    }

    public void writeMatrix(DoubleMatrix2D matrix) throws IOException {
        double[][] a = matrix.toArray();
        try (PrintWriter out = new PrintWriter(new FileWriter("amatrix.csv"))) {
            for (int r = 0; r < a.length; r++) {
                double[] row = a[r];
                for (int col = 0; col < row.length; col++) {
                    if (row[col] == 0) {
                        out.print("0");
                    } else {
                        out.print(row[col]);
                    }
                    if (col < row.length - 1) {
                        out.print(",");
                    }
                }
                out.println();
            }
        }
    }

    List<Object[]> readMatrix(String fileName) throws IOException {
        CSVFile<Double> csv = CSVFile.readFileNumeric(new File(fileName), true, 0);
        List<Object[]> result = new ArrayList<>();
        for (int r = 0; r < csv.getNumRows(); r++) {
            result.add(csv.getLine(r).toArray());
        }
        return result;
    }

